 /**
 * Javascript for some theme options.
 */
jQuery(document).ready(function(){var selected=0;if(jQuery.cookie('travelify_tab')){selected=jQuery.cookie('travelify_tab');jQuery.cookie('travelify_tab',null)}var tabs=jQuery('#travelify_tabs').tabs({selected:selected});jQuery('#wpbody-content form').submit(function(){var selected=tabs.tabs('option','selected');jQuery.cookie('travelify_tab',selected)});jQuery('.sortable').sortable({handle:'label',update:function(event,ui){var index=1;var attrname=jQuery(this).find('input:first').attr('name');var attrbase=attrname.substring(0,attrname.indexOf('][')+1);jQuery(this).find('tr').each(function(){jQuery(this).find('.count').html(index);jQuery(this).find('input').attr('name',attrbase+'['+index+']');index++})}})});


/**
 * Show Hide Toggle Box
 */
jQuery(document).ready(function($){$(".option-content").hide();$("h3.option-toggle").click(function(){$(this).toggleClass("option-active").next().slideToggle("fast");return false})});jQuery(document).ready(function($){setTimeout(function(){$(".fade").fadeOut("slow",function(){$(".fade").remove()})},1000)});
